mapper.select:
select

from sso_area


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}


			and code = #{record.code,jdbcType=VARCHAR}


			and name = #{record.name,jdbcType=VARCHAR}


			and parent_id = #{record.parentId,jdbcType=VARCHAR}


			and parent_code = #{record.parentCode,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_area
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, del_flag = #{delFlag,jdbcType=INTEGER}, code = #{code,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=VARCHAR}, parent_code = #{parentCode,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
