mapper.select:
select

from sso_dept_ext


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and dept_id = #{record.deptId,jdbcType=VARCHAR}


			and dept_head_user_id = #{record.deptHeadUserId,jdbcType=VARCHAR}


			and dept_head_name = #{record.deptHeadName,jdbcType=VARCHAR}


			and dept_head_mobile = #{record.deptHeadMobile,jdbcType=VARCHAR}


			and social_credit_code = #{record.socialCreditCode,jdbcType=VARCHAR}


			and registered_address = #{record.registeredAddress,jdbcType=VARCHAR}


			and dept_opt_name = #{record.deptOptName,jdbcType=VARCHAR}


			and dept_opt_mobile = #{record.deptOptMobile,jdbcType=VARCHAR}


			and work_place = #{record.workPlace,jdbcType=VARCHAR}


			and dept_address_province = #{record.deptAddressProvince,jdbcType=VARCHAR}


			and dept_address_city = #{record.deptAddressCity,jdbcType=VARCHAR}


			and dept_address_area = #{record.deptAddressArea,jdbcType=VARCHAR}


			and dept_address_detail = #{record.deptAddressDetail,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_dept_ext
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, dept_id = #{deptId,jdbcType=VARCHAR}, dept_head_user_id = #{deptHeadUserId,jdbcType=VARCHAR}, dept_head_name = #{deptHeadName,jdbcType=VARCHAR}, dept_head_mobile = #{deptHeadMobile,jdbcType=VARCHAR}, social_credit_code = #{socialCreditCode,jdbcType=VARCHAR}, registered_address = #{registeredAddress,jdbcType=VARCHAR}, dept_opt_name = #{deptOptName,jdbcType=VARCHAR}, dept_opt_mobile = #{deptOptMobile,jdbcType=VARCHAR}, work_place = #{workPlace,jdbcType=VARCHAR}, dept_address_province = #{deptAddressProvince,jdbcType=VARCHAR}, dept_address_city = #{deptAddressCity,jdbcType=VARCHAR}, dept_address_area = #{deptAddressArea,jdbcType=VARCHAR}, dept_address_detail = #{deptAddressDetail,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
