mapper.select:
select

from sso_dept


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and org_id = #{record.orgId,jdbcType=VARCHAR}


			and name = #{record.name,jdbcType=VARCHAR}


			and code = #{record.code,jdbcType=VARCHAR}


			and parent_dept_id = #{record.parentDeptId,jdbcType=VARCHAR}


			and parent_dept_code = #{record.parentDeptCode,jdbcType=VARCHAR}


			and levels = #{record.levels,jdbcType=INTEGER}


			and org_flag = #{record.orgFlag,jdbcType=INTEGER}


			and creation_time = #{record.creationTime,jdbcType=TIMESTAMP}


			and enable_flag = #{record.enableFlag,jdbcType=INTEGER}


			and type = #{record.type,jdbcType=INTEGER}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}


			and order_id = #{record.orderId,jdbcType=INTEGER}



	order by ${orderBy}

mapper.update:
update sso_dept
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, org_id = #{orgId,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, parent_dept_id = #{parentDeptId,jdbcType=VARCHAR}, parent_dept_code = #{parentDeptCode,jdbcType=VARCHAR}, levels = #{levels,jdbcType=INTEGER}, org_flag = #{orgFlag,jdbcType=INTEGER}, creation_time = #{creationTime,jdbcType=TIMESTAMP}, enable_flag = #{enableFlag,jdbcType=INTEGER}, type = #{type,jdbcType=INTEGER}, del_flag = #{delFlag,jdbcType=INTEGER}, order_id = #{orderId,jdbcType=INTEGER},
where id = #{id,jdbcType=VARCHAR}
