mapper.select:
select

from sso_dict


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and dict_prefix = #{record.dictPrefix,jdbcType=VARCHAR}


			and dict_sn = #{record.dictSn,jdbcType=VARCHAR}


			and dict_data = #{record.dictData,jdbcType=INTEGER}


			and dict_desc = #{record.dictDesc,jdbcType=VARCHAR}


			and system_code = #{record.systemCode,jdbcType=VARCHAR}


			and enable_flag = #{record.enableFlag,jdbcType=INTEGER}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}


			and order_id = #{record.orderId,jdbcType=INTEGER}



	order by ${orderBy}

mapper.update:
update sso_dict
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, dict_prefix = #{dictPrefix,jdbcType=VARCHAR}, dict_sn = #{dictSn,jdbcType=VARCHAR}, dict_data = #{dictData,jdbcType=INTEGER}, dict_desc = #{dictDesc,jdbcType=VARCHAR}, system_code = #{systemCode,jdbcType=VARCHAR}, enable_flag = #{enableFlag,jdbcType=INTEGER}, del_flag = #{delFlag,jdbcType=INTEGER}, order_id = #{orderId,jdbcType=INTEGER},
where id = #{id,jdbcType=VARCHAR}
