mapper.select:
select

from sso_menu


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and system_code = #{record.systemCode,jdbcType=VARCHAR}


			and name = #{record.name,jdbcType=VARCHAR}


			and parent_id = #{record.parentId,jdbcType=VARCHAR}


			and url_path = #{record.urlPath,jdbcType=VARCHAR}


			and url_type = #{record.urlType,jdbcType=INTEGER}


			and permission_code = #{record.permissionCode,jdbcType=VARCHAR}


			and component = #{record.component,jdbcType=VARCHAR}


			and hide_flag = #{record.hideFlag,jdbcType=INTEGER}


			and sort = #{record.sort,jdbcType=INTEGER}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}


			and enable_flag = #{record.enableFlag,jdbcType=INTEGER}


			and url_meta = #{record.urlMeta,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_menu
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, system_code = #{systemCode,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=VARCHAR}, url_path = #{urlPath,jdbcType=VARCHAR}, url_type = #{urlType,jdbcType=INTEGER}, permission_code = #{permissionCode,jdbcType=VARCHAR}, component = #{component,jdbcType=VARCHAR}, hide_flag = #{hideFlag,jdbcType=INTEGER}, sort = #{sort,jdbcType=INTEGER}, del_flag = #{delFlag,jdbcType=INTEGER}, enable_flag = #{enableFlag,jdbcType=INTEGER}, url_meta = #{urlMeta,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
