mapper.select:
select

from sso_system


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and system_code = #{record.systemCode,jdbcType=VARCHAR}


			and system_desc = #{record.systemDesc,jdbcType=VARCHAR}


			and system_model_code = #{record.systemModelCode,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_system
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, system_code = #{systemCode,jdbcType=VARCHAR}, system_desc = #{systemDesc,jdbcType=VARCHAR}, system_model_code = #{systemModelCode,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
