mapper.select:
select

from sso_user_job


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and user_id = #{record.userId,jdbcType=VARCHAR}


			and dept_id = #{record.deptId,jdbcType=VARCHAR}


			and post_id = #{record.postId,jdbcType=VARCHAR}


			and begin_time = #{record.beginTime,jdbcType=DATE}


			and end_time = #{record.endTime,jdbcType=DATE}


			and last_flag = #{record.lastFlag,jdbcType=INTEGER}


			and main_flag = #{record.mainFlag,jdbcType=INTEGER}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}



	order by ${orderBy}

mapper.update:
update sso_user_job
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, user_id = #{userId,jdbcType=VARCHAR}, dept_id = #{deptId,jdbcType=VARCHAR}, post_id = #{postId,jdbcType=VARCHAR}, begin_time = #{beginTime,jdbcType=DATE}, end_time = #{endTime,jdbcType=DATE}, last_flag = #{lastFlag,jdbcType=INTEGER}, main_flag = #{mainFlag,jdbcType=INTEGER}, del_flag = #{delFlag,jdbcType=INTEGER},
where id = #{id,jdbcType=VARCHAR}
