mapper.select:
select

from sso_user_login_history


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and login_ip = #{record.loginIp,jdbcType=VARCHAR}


			and login_time = #{record.loginTime,jdbcType=TIMESTAMP}


			and device = #{record.device,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_user_login_history
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, login_ip = #{loginIp,jdbcType=VARCHAR}, login_time = #{loginTime,jdbcType=TIMESTAMP}, device = #{device,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
