mapper.select:
select

from sso_user


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and name = #{record.name,jdbcType=VARCHAR}


			and code = #{record.code,jdbcType=VARCHAR}


			and status = #{record.status,jdbcType=INTEGER}


			and photo = #{record.photo,jdbcType=VARCHAR}


			and id_number = #{record.idNumber,jdbcType=VARCHAR}


			and hired_date = #{record.hiredDate,jdbcType=DATE}


			and email = #{record.email,jdbcType=VARCHAR}


			and mobile = #{record.mobile,jdbcType=VARCHAR}


			and order_id = #{record.orderId,jdbcType=INTEGER}


			and del_flag = #{record.delFlag,jdbcType=INTEGER}


			and last_login_ip = #{record.lastLoginIp,jdbcType=VARCHAR}


			and last_login_time = #{record.lastLoginTime,jdbcType=TIMESTAMP}


			and dept_id = #{record.deptId,jdbcType=VARCHAR}


			and dept_code = #{record.deptCode,jdbcType=VARCHAR}


			and dept_name = #{record.deptName,jdbcType=VARCHAR}


			and post_id = #{record.postId,jdbcType=VARCHAR}


			and post_code = #{record.postCode,jdbcType=VARCHAR}


			and post_name = #{record.postName,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_user
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, name = #{name,jdbcType=VARCHAR}, code = #{code,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, photo = #{photo,jdbcType=VARCHAR}, id_number = #{idNumber,jdbcType=VARCHAR}, hired_date = #{hiredDate,jdbcType=DATE}, email = #{email,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, order_id = #{orderId,jdbcType=INTEGER}, del_flag = #{delFlag,jdbcType=INTEGER}, last_login_ip = #{lastLoginIp,jdbcType=VARCHAR}, last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}, dept_id = #{deptId,jdbcType=VARCHAR}, dept_code = #{deptCode,jdbcType=VARCHAR}, dept_name = #{deptName,jdbcType=VARCHAR}, post_id = #{postId,jdbcType=VARCHAR}, post_code = #{postCode,jdbcType=VARCHAR}, post_name = #{postName,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
