mapper.select:
select

from sso_user_security


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and user_id = #{record.userId,jdbcType=VARCHAR}


			and login_able = #{record.loginAble,jdbcType=INTEGER}


			and password = #{record.password,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_user_security
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, user_id = #{userId,jdbcType=VARCHAR}, login_able = #{loginAble,jdbcType=INTEGER}, password = #{password,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
