mapper.select:
select

from sso_user_third_party


			and id = #{record.id,jdbcType=VARCHAR}


			and create_user_id = #{record.createUserId,jdbcType=VARCHAR}


			and create_time = #{record.createTime,jdbcType=TIMESTAMP}


			and update_user_id = #{record.updateUserId,jdbcType=VARCHAR}


			and update_time = #{record.updateTime,jdbcType=TIMESTAMP}


			and user_id = #{record.userId,jdbcType=VARCHAR}


			and app_id = #{record.appId,jdbcType=VARCHAR}


			and enable_flag = #{record.enableFlag,jdbcType=INTEGER}


			and app_user_id = #{record.appUserId,jdbcType=VARCHAR}


			and app_union_id = #{record.appUnionId,jdbcType=VARCHAR}


			and app_open_id = #{record.appOpenId,jdbcType=VARCHAR}



	order by ${orderBy}

mapper.update:
update sso_user_third_party
create_user_id = #{createUserId,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_user_id = #{updateUserId,jdbcType=VARCHAR}, update_time = #{updateTime,jdbcType=TIMESTAMP}, user_id = #{userId,jdbcType=VARCHAR}, app_id = #{appId,jdbcType=VARCHAR}, enable_flag = #{enableFlag,jdbcType=INTEGER}, app_user_id = #{appUserId,jdbcType=VARCHAR}, app_union_id = #{appUnionId,jdbcType=VARCHAR}, app_open_id = #{appOpenId,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}
